import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { z } from "zod";

export const categorySchema = z.object({
  id: z.string(),
  name: z.string().min(1, "Nome √© obrigat√≥rio"),
  type: z.enum(["income", "expense"]),
  color: z.string().regex(/^#[0-9A-F]{6}$/i, "Cor deve ser um hex v√°lido"),
  icon: z.string().min(1, "√çcone √© obrigat√≥rio"),
  createdAt: z.date(),
});

export const transactionSchema = z.object({
  id: z.string(),
  date: z.date(),
  categoryId: z.string().min(1, "Selecione uma categoria"),
  amount: z.number().positive("Informe um valor maior que zero"),
  description: z.string().optional(),
  type: z.enum(["income", "expense"]),
  createdAt: z.date(),
});

export const settingsSchema = z.object({
  id: z.string().default("settings"),
  weeklyGoal: z.number().positive("Meta semanal deve ser positiva"),
  weekStartsOn: z.number().min(0).max(6).default(0), // 0 = Sunday
  theme: z.enum(["light", "dark", "system"]).default("system"),
  currency: z.string().default("BRL"),
  createdAt: z.date(),
  updatedAt: z.date(),
});

// Insert schemas
export const insertCategorySchema = categorySchema.omit({ id: true, createdAt: true });
export const insertTransactionSchema = transactionSchema.omit({ id: true, createdAt: true });
export const insertSettingsSchema = settingsSchema.omit({ id: true, createdAt: true, updatedAt: true });

// Types
export type Category = z.infer<typeof categorySchema>;
export type Transaction = z.infer<typeof transactionSchema>;
export type Settings = z.infer<typeof settingsSchema>;
export type InsertCategory = z.infer<typeof insertCategorySchema>;
export type InsertTransaction = z.infer<typeof insertTransactionSchema>;
export type InsertSettings = z.infer<typeof insertSettingsSchema>;

// Backup schema
export const backupSchema = z.object({
  version: z.string(),
  exportDate: z.date(),
  categories: z.array(categorySchema),
  transactions: z.array(transactionSchema),
  settings: settingsSchema,
});

export type BackupData = z.infer<typeof backupSchema>;

